import tkinter as tk

# --- functions ---

def on_click(args):
    widget,x,y = args[0],args[1],args[2]
    if widget['image'] == 'pyimage1':
        widget['image'] = img2
        print(widget['image'])
        print(x,y,1)
    else:
        widget['image'] = img1
        print(widget['image'])
        print(x,y,0)

# --- main ---

root = tk.Tk()

img1 = tk.PhotoImage( data ='')
img2 = tk.PhotoImage(data = '')


for y in range(10):
    for x in range(12):
        button = tk.Button(root, image=img1)
        button['command'] = lambda args=[button, x,y]:on_click(args)
        button.grid(row=y, column=x)

root.mainloop()